import React, { useState } from "react";
import PropTypes from "prop-types";
import styles from "./App.module.css";
import InputForm from "./InputForm";

const App = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [password, setPassword] = useState("");

  const btnClick = () => {};

  return (
    <div className={styles.full_screen}>
      <div className={styles.wrapper}>
        <h1>회원가입</h1>
        <div className={styles.input_div}>
          <InputForm
            label="이름"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <InputForm
            label="이메일"
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <InputForm
            label="나이"
            type="number"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
          <InputForm
            label="비밀번호"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <InputForm
            label="비밀번호확인"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <button onClick={btnClick}>가입하기</button>
        </div>
      </div>

      <div className={styles.modalWrapper} display="none">
        <div className={styles.modal}>
          <h1 className={styles.modalTitle}>가입 성공!</h1>
          <p className={styles.modalContant}>UMC 챌린저 가입을 축하합니다!!!</p>
          <button className={styles.closeBtn}>닫기</button>
        </div>
      </div>
    </div>
  );
};

export default App;
