import React, { useState } from "react";
import PropTypes from "prop-types";
import styles from "./App.module.css";
import InputForm from "./InputForm";
import Modal from "./Modal/Modal";

const App = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPwd, setConfrimPwd] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showModal, setShowModal] = useState(false);

  const openModal = setShowModal(true);
  const closeModal = setShowModal(false);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleAgeChange = (e) => {
    const value = e.target.value;
    if (
      !isNaN(value) &&
      value !== "" &&
      Number(value) >= 0 &&
      Number.isInteger(Number(value))
    ) {
      setAge(value);
    } else {
      setFormErrors([...formErrors, "나이는 양수의 정수여야 합니다."]);
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors([]);

    if (!name) {
      setFormErrors([...formErrors, "이름을 입력하세요."]);
    }

    if (!email || !email.includes("@")) {
      setFormErrors([...formErrors, "올바른 이메일을 입력하세요."]);
    }

    if (!age) {
      setFormErrors([...formErrors, "나이를 입력하세요."]);
    }

    if (age && parseInt(age) < 19) {
      setFormErrors([
        ...formErrors,
        "우리 영화 사이트는 19세 이상만 가입 가능합니다.",
      ]);
    }

    if (!password) {
      setFormErrors([...formErrors, "비밀번호를 입력하세요."]);
    }

    if (password.length < 4) {
      setFormErrors([...formErrors, "비밀번호는 4자리 이상이어야 합니다."]);
    }

    if (password.length > 12) {
      setFormErrors([...formErrors, "비밀번호는 12자리까지 가능합니다."]);
    }

    if (
      !/(?=.*[a-zA-Z])(?=.*\d)(?=.*[!@#$%^&*()_+])[A-Za-z\d!@#$%^&*()_+]{4,12}$/.test(
        password
      )
    ) {
      setFormErrors([
        ...formErrors,
        "비밀번호는 영어, 숫자, 특수문자를 조합하여야 합니다.",
      ]);
    }

    if (password !== confirmPassword) {
      setFormErrors([...formErrors, "비밀번호 확인이 일치하지 않습니다."]);
    }

    if (formErrors.length === 0) {
      // 여기서 회원가입 처리
      console.log("회원가입 정보가 유효합니다.");
    }
  };

  const btnClick = () => {
    openModal();
  };

  return (
    <div className={styles.full_screen}>
      <div className={styles.wrapper}>
        <h1>회원가입</h1>
        <div className={styles.input_div}>
          <InputForm
            label="이름"
            type="text"
            value={name}
            onChange={handleNameChange}
          />
          <InputForm
            label="이메일"
            type="text"
            value={email}
            onChange={handleEmailChange}
          />
          <InputForm
            label="나이"
            type="number"
            value={age}
            onChange={handleAgeChange}
          />
          <InputForm
            label="비밀번호"
            type="password"
            value={password}
            onChange={handlePasswordChange}
          />
          <InputForm
            label="비밀번호확인"
            type="password"
            value={password}
            onChange={handleConfirmPwdChange}
          />
        </div>
        <div>
          <button onClick={btnClick} disabled={errorMessage.length > 0}>
            가입하기
          </button>
        </div>
      </div>

      {showModal && <Modal closeModal={closeModal} />}
    </div>
  );
};

export default App;
