import React from "react";
import styles from "./App.module.css";
import InputForm from "./InputForm";

const App = () => {
  return (
    <div className={styles.screen}>
      <div className={styles.mapper}>
        <h1>회원가입</h1>
        <div className={styles.input - divr}>
          <InputForm
            label="이름"
            type="text"
            value={name}
            onChange={(e) => setWord(e.target.value)}
          />
          <InputForm
            label="이메일"
            type="text"
            value={email}
            onChange={(e) => setMeaning(e.target.value)}
          />
          <InputForm
            label="나이"
            type="number"
            value={agel}
            onChange={(e) => setDetail(e.target.value)}
          />
          <InputForm
            label="비밀번호"
            type="password"
            value={password}
            onChange={(e) => setDetail(e.target.value)}
          />
          <InputForm
            label="비밀번호확인"
            type="password"
            value={password}
            onChange={(e) => setDetail(e.target.value)}
          />
        </div>
        <div>
          <button>가입하기</button>
        </div>
      </div>
    </div>
  );
};

export default App;
