import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; 
import Loader from "../components/Loader";
import styled from "styled-components";

const Wrapper = styled.div`
  background-color: rgb(70, 70, 130);
  height: 350px;
  width: 180px;
  border-radius: 5px;
  margin: 5px;
  position: relative;
`;

const Image = styled.img`
  height: 270px;
  width: 180px;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
`;

const MovieInfo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h2`
  width: 100px;
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const VoteAverage = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const ReleaseDate = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const Overview = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const MovieDetailPage = () => {

  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState([]);

  const { title } = useParams();

  useEffect(() => {
      const options = {
        method: 'GET',
        headers: {
          accept: 'application/json',
          Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1NzJlMTc4NmMzOTRiNTNiNDE0YjI4OWY0ZWU5YTk4NyIsInN1YiI6IjY2MzM0N2U5ODEzY2I2MDEyMTg2ZTlhYiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.A159m_rOX_HnuFPW_aHnMOTiMYyXhQbT3yMamEeazCg'
        }
      };

      fetch(`https://api.themoviedb.org/3/movie_title=${title}`, options)
        .then(response => response.json())
        .then(data => {
          setMovie(data.results); // API 호출 결과를 movies 상태에 저장
          setLoading(false); // 로딩 상태 변경
        })
        .catch(err => {
          console.error(err);
          setLoading(false); // 에러 발생 시 로딩 상태 변경
        });
    }, [title]); // title이 변경될 때마다 useEffect 재실행

    return (
      <div>
        {loading ? (
          <Loader />
        ) : (
          <Wrapper>
            <Image src={movie.poster_path} alt={movie.title} />
            <MovieInfo>
              <Title>{movie.title}</Title>
              <VoteAverage>{movie.vote_average}</VoteAverage>
              <ReleaseDate>{movie.release_date}</ReleaseDate>
              <Overview>{movie.overview}</Overview>
            </MovieInfo>
            <button>Back to List</button>
          </Wrapper>
        )}
      </div>
    );
};

export default MovieDetailPage;
