import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Loader from "../components/Loader";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";

const Wrapper = styled.div`
  background-color: rgb(70, 70, 130);
  height: 100vh;
  width: 100vw;
  position: fixed;
  top: 0px;
  left: 0px;
`;

const Image = styled.img`
  height: 500px;
  width: 300px;
`;

const MovieInfo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h2`
  width: 100px;
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const VoteAverage = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const ReleaseDate = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const Overview = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const BackButton = styled.button`
  margin: 10px;
`;

const MovieDetailPage = () => {
  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState(null);
  const { title } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const response = await fetch(`https://api.themoviedb.org/3/movie/${encodeURIComponent(title)}`, {
          headers: {
            Authorization: "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1NzJlMTc4NmMzOTRiNTNiNDE0YjI4OWY0ZWU5YTk4NyIsInN1YiI6IjY2MzM0N2U5ODEzY2I2MDEyMTg2ZTlhYiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.A159m_rOX_HnuFPW_aHnMOTiMYyXhQbT3yMamEeazCg"
          }
        });
        if (response.ok) {
          const data = await response.json();
          setMovie(data);
          setLoading(false);
        } else {
          throw new Error("Failed to fetch movie details");
        }
      } catch (error) {
        console.error(error);
        setLoading(false);
      }
    };

    fetchMovie();
  }, [title]);

  const handleBackClick = () => {
    navigate(-1); // 뒤로 가기 버튼 클릭 시 이전 페이지로 이동
  };

  return (
    <Wrapper>
      {loading ? (
        <Loader />
      ) : (
        <>
          <Image src={movie.poster_path} alt={movie.title} />
          <MovieInfo>
            <Title>{movie.title}</Title>
            <VoteAverage>{movie.vote_average}</VoteAverage>
            <ReleaseDate>{movie.release_date}</ReleaseDate>
            <Overview>{movie.overview}</Overview>
          </MovieInfo>
          <BackButton onClick={handleBackClick}>Back to List</BackButton>
        </>
      )}
    </Wrapper>
  );
};

export default MovieDetailPage;
