import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; 
import Loader from "../components/Loader";
import styled from "styled-components";

const Wrapper = styled.div`
  background-color: rgb(35, 35, 70);
  height: 100vh;
  width: 100vw;
  position: fixed;
  top: 0px;
  left: 0px;
`;

const Image = styled.img`
  height: 500px;
  width: 300px;
`;

const MovieInfo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Title = styled.h2`
  width: 100px;
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const VoteAverage = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const ReleaseDate = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const Overview = styled.span`
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const MovieDetailPage = () => {
  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState(null);
  const { id } = useParams();

  useEffect(() => {

    const options = {
      method: 'GET',
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1NzJlMTc4NmMzOTRiNTNiNDE0YjI4OWY0ZWU5YTk4NyIsInN1YiI6IjY2MzM0N2U5ODEzY2I2MDEyMTg2ZTlhYiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.A159m_rOX_HnuFPW_aHnMOTiMYyXhQbT3yMamEeazCg'
      }
    };

    fetch(`https://api.themoviedb.org/3/movie/movie_id?language=en-US', options`, options)
      .then(response => response.json())
      .then(data => {
        if (data.results.length > 0) {
          setMovie(data.results[0]);
          setLoading(false);
        } else {
          console.error("No movie found with that title");
          setLoading(false);
        }
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  return (
    <Wrapper>
      {loading ? (
        <Loader />
      ) : (
        movie && (
          <>
            <Image src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
            <MovieInfo>
              <Title>{movie.title}</Title>
              <VoteAverage>{movie.vote_average}</VoteAverage>
              <ReleaseDate>{movie.release_date}</ReleaseDate>
              <Overview>{movie.overview}</Overview>
            </MovieInfo>
          </>
        )
      )}
    </Wrapper>
  );
};

export default MovieDetailPage;
