import React from "react";
import styled from "styled-components";

const ModalWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ModalContainer = styled.div`
  background: white;
  padding: 15px;
  border-radius: 5px;
  width: 300px;
  align-items: center;
  justify-content: center;
`;

const ModalTitle = styled.h1`
  font-size: 18px;
`;

const ModalContent = styled.p`
  font-size: 15px;
`;

const CloseDiv = styled.div`
  height: 30px;
  position: relative;
`;

const CloseButton = styled.button`
  height: 25px;
  width: 70px;
  border-radius: 7px;
  border: none;
  position: absolute;
  right: 5px;
`;

const Modal = ({ closeModal }) => {
  return (
    <ModalWrapper>
      <ModalContainer>
        <ModalTitle>가입 성공!</ModalTitle>
        <ModalContent>UMC 챌린저 가입을 축하합니다!!!</ModalContent>
        <CloseDiv>
          <CloseButton onClick={closeModal}>닫기</CloseButton>
        </CloseDiv>
      </ModalContainer>
    </ModalWrapper>
  );
};

export default Modal;
