import React, { useState } from "react";
import styled from "styled-components";

const Banner = styled.div`
  display: flex;
  height: 40px;
  width: 100%;
  padding: 30px;
  position: fixed;
  left: 0px;
  top: 0px;
  background-color: rgb(70, 70, 130);
`;

const LeftColumn = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-grow: 1;
`;

const RightColumn = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  flex-grow: 1;
`;

const TabText = styled.span`
  font-size: 13px;
  padding: 15px;
  color: ${({ selected }) => (selected ? "yellow" : "white")};
  transition: all 0.3s ease-in-out;

  &:hover {
    transform: scale(1.1);
    cursor: pointer;
  }
`;


const MainPage = () => {

  return (
    <div>
        <Banner>환영합니다</Banner>
        <h3>Find your movies !</h3>
    </div>
  );
};

export default MainPage;
