import React, { useState } from "react";
import PropTypes from "prop-types";
import styled from "styled-components";
import MovieDetail from "./MovieDetail";

const Wrapper = styled.div`
  background-color: rgb(70, 70, 130);
  height: 350px;
  width: 180px;
  border-radius: 5px;
  margin: 5px;
  position: relative;
`;

const Image = styled.img`
  height: 270px;
  width: 180px;
`;

const Title = styled.h2`
  width: 100px;
  margin: 10px 10px;
  font-size: 13px;
  color: white;
`;

const VoteAverage = styled.span`
  margin: 10px 5px;
  font-size: 13px;
  color: white;
`;

const Movie = ({ poster_path, title, overview, vote_average }) => {
  const [showDetail, setShowDetail] = useState(false);

  const openDetail = () => setShowDetail(true);
  const closeDetail = () => setShowDetail(false);

  return (
    <Wrapper onMouseOver={openDetail} onMouseOut={closeDetail}>
      <Image src={poster_path} alt={title} />
      <Title>{title}</Title>
      <VoteAverage>{vote_average}</VoteAverage>
      {showDetail && <MovieDetail title={title} overview={overview} />}
    </Wrapper>
  );
};

Movie.propTypes = {
  poster_path: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  overview: PropTypes.string.isRequired,
  vote_average: PropTypes.number.isRequired
};

export default Movie;
