    }

    return (
      <Screen>
        <Wrapper>
          <Header>Log In</Header>
          <InputDiv>
            <LogInInputForm
              label="Email"
              type="text"
              value={id}
              onChange={handleIdChange}
            />
            <LogInInputForm
              label="Password"
              type="password"
              value={password}
              onChange={handlePasswordChange}
            />
          </InputDiv>
          <div>
            <LoginButton onClick={handleLogin}>Log In</LoginButton>
          </div>
        </Wrapper>
      </Screen>
    );
};

export default LogInPage;
