import React, { useState } from "react";
import styled from "styled-components";

const Banner = styled.div`
  display: flex;
  height: 250px;
  width: 100%;
  color: white;
  font-size: 20px;
  background-color: rgba(0, 0, 0, 0.6);
  justify-content: center;
  align-items: center;
  position: absolute;
`;

const Text = styled.span`
  display: flex;
  width: 100%;
  color: white;
  font-size: 20px;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 270px;
`;


const MainPage = () => {

  return (
    <div>
        <Banner>환영합니다</Banner>
        <Text>Find your movies !</Text>
    </div>
  );
};

export default MainPage;
