import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";

const NavbarWrapper = styled.div`
  display: flex;
  height: 40px;
  width: 100vh;
  padding: 5px;
  justify-content: center;
  position: fixed;
  top: 0px;
  background-color: rgb(70, 70, 130);
`;

const Column = styled.div`
  width: 33%;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.3s ease-in-out;

  &:hover {
    transform: scale(1.1);
    cursor: pointer;
  }
`;

const CenteredContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TabText = styled.span`
  font-size: 13px;
  padding: 5px;
  color: ${({ selected }) => (selected ? "yellow" : "white")};
`;

const Navbar = () => {
  const navigate = useNavigate();
  const [selectedTab, setSelectedTab] = useState("/");

  const handleTabClick = (path) => {
    navigate(path);
    setSelectedTab(path);
  };

  const Tab = ({ path, label }) => {
    const selected = selectedTab === path;

    return (
      <Column onClick={() => handleTabClick(path)}>
        <CenteredContent>
          <TabText selected={selected}>{label}</TabText>
        </CenteredContent>
      </Column>
    );
  };

  return (
    <NavbarWrapper>
      <Tab path="/" label="UMC Movie" />
      <Tab path="/signIn" label="회원가입" />
      <Tab path="/popular" label="Popular" />
      <Tab path="/nowPlaying" label="Now Playing" />
      <Tab path="/topRated" label="Top Rated" />
      <Tab path="/upComing" label="Upcoming" />
    </NavbarWrapper>
  );
};

export default Navbar;
