import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";

const NavbarWrapper = styled.div`
  display: flex;
  height: 40px;
  width: 100%;
  padding: 3px;
  position: fixed;
  left: 0px;
  top: 0px;
  background-color: rgb(70, 70, 130);
`;

const LeftColumn = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding: 3px;
  flex-grow: 1;
`;

const RightColumn = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 3px;
  flex-grow: 1;
`;

const TabText = styled.span`
  font-size: 13px;
  padding: 5px;
  color: ${({ selected }) => (selected ? "yellow" : "white")};
  transition: all 0.3s ease-in-out;

  &:hover {
    transform: scale(1.1);
    cursor: pointer;
  }
`;

const Navbar = () => {
  const navigate = useNavigate();
  const [selectedTab, setSelectedTab] = useState("/");

  const handleTabClick = (path) => {
    navigate(path);
    setSelectedTab(path);
  };

  const Tab = ({ path, label }) => {
    const selected = selectedTab === path;

    return (
      <TabText
        onClick={() => handleTabClick(path)}
        onTouchMoveCapture={() => handleTabClick(path)}
        selected={selected}
      >
        {label}
      </TabText>
    );
  };

  return (
    <NavbarWrapper>
      <LeftColumn>
        <Tab path="/" label="UMC Movie" />
      </LeftColumn>
      <RightColumn>
        <Tab path="/signIn" label="회원가입" />
        <Tab path="/popular" label="Popular" />
        <Tab path="/nowPlaying" label="Now Playing" />
        <Tab path="/topRated" label="Top Rated" />
        <Tab path="/upComing" label="Upcoming" />
      </RightColumn>
    </NavbarWrapper>
  );
};

export default Navbar;
