            value={id}
            onChange={handleNameChange}
            message={nameMessage}
            success={nameSuccess}
          />
          <InputForm
            label="비밀번호"
            type="password"
            value={password}
            onChange={handlePasswordChange}
            message={passwordMessage}
            success={passwordSuccess}
          />
        </InputDiv>
        <div>
          <LoginButton valid={valid} onClick={handleLogin}>로그인</LoginButton>
        </div>
      </Wrapper>
  );
};

export default LogInPage;
